class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> diff(n);
        for(int i=0;i<n;i++){
            diff[i]=target[i]-nums[i];
        }
        long long int count  = abs(diff[0]);
        bool flag = (diff[0] >= 0) ? true:false;
        for(int i=1;i<n;i++){
            if(flag){
                if(diff[i] < 0){
                    flag = false;
                    count += (long long) abs(diff[i]);
                }
                else if(diff[i]<=diff[i-1])
                    continue;
                else{
                    count+= (long long) diff[i]-diff[i-1];
                }
            }
            else {
                if(diff[i] >= 0){
                    flag = true;
                    count +=(long long) abs(diff[i]);
                }
                else if(abs(diff[i])<=abs(diff[i-1]))
                    continue;
                else{
                    count+= (long long)abs((long long)diff[i]-diff[i-1]);
                }
            }
        }
        return count;
    }
};
