class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long pd = 0;
        long long ans = 0;
        for (int i = 0 ; i < nums.size() ; ++i) {
            long long d = target[i] - nums[i];
            if (d * pd >= 0) {
                if (abs(d) >= abs(pd)) ans += abs(d) - abs(pd);
            } else {
                ans += abs(d);
            }
            pd = d;
        }
        return ans;
    }
};
