class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        for (int i=0; i<n; i++){
            nums[i] = target[i] - nums[i];
        }
        long long int temp = nums[0], ans = abs(nums[0]);
        for (int i=1; i<n; i++){
            if ((temp >= 0 && nums[i]>=0) || (temp <= 0 && nums[i]<=0)){
                if (abs(temp) < abs(nums[i])){
                    ans += abs(nums[i] - temp);
                }
            }
            else{
                ans += abs(nums[i]);
            }
            temp = nums[i];
        }
        return ans;
    }
};
