class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> del(n, 0);
        for (int i = 0; i < n; i++)
        {
            del[i] = target[i] - nums[i];
        }
        long long ans = 0, cur = 0;
        for (int i = 0; i < n; i++)
        {
            if (cur > 0 && del[i] > 0)
            {
                ans += (del[i] > cur) ? (del[i] - cur) : 0;
            }
            else if (cur < 0 && del[i] < 0)
            {
                ans += (del[i] < cur) ? (cur - del[i]) : 0;
            }
            else ans += abs(del[i]);

            cur = del[i];
        }
        return ans;
    }
};
