class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ops = 0;
        int i = 0;
        while(i<nums.size()){
            if(nums[i] == target[i]){
                i++;
                continue;
            }
            if(nums[i]>target[i]){
                int maxdiff = 0;
                while(i<nums.size() && nums[i]>target[i]){
                    int diff = abs(nums[i]-target[i]);
                    if(diff<maxdiff){
                        ops += (maxdiff-diff);
                        maxdiff = diff;
                    }
                    else maxdiff = max(maxdiff,diff);
                    i++;
                }
                ops += maxdiff;
            }
            else{
                int maxdiff = 0;
                while(i<nums.size() && nums[i]<target[i]){
                    int diff = abs(nums[i]-target[i]);
                    if(diff<maxdiff){
                        ops += (maxdiff-diff);
                        maxdiff = diff;
                    }
                    else maxdiff = max(maxdiff,diff);
                    i++;
                }
                ops += maxdiff;
            }
        }
        return ops;
    }
};
