class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long int inc=0,dec=0;
        long long ans=0;
        for(int i=0;i<nums.size();i++){
            if(target[i]>nums[i]){
                ans= ans+max((long long)0,(long long)(target[i]-nums[i])-inc);
                inc = (target[i]-nums[i]);
                dec=0;
            }
            else{
                inc=0;
                ans= ans+ max((long long)0,(long long)abs(target[i]-nums[i])-dec);
                dec = abs(target[i]-nums[i]);
            }
        }
        return ans;
    }
};
