class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {

           long long res = 0;
           vector<int> tmp;
           vector<vector<int>> ans;
           int f = 0;
           int f1 = 0;

           for(int i=0;i<nums.size();i++)
           { 
                if(nums[i]==target[i])
                {
                if(tmp.size())
                ans.push_back(tmp);
                tmp.clear();
                f1 = 0;
                f = 0;
                }
                if(nums[i]>target[i])
                {
                    f1 = 1;
                    if(f==1)
                    {
                        ans.push_back(tmp);
                        tmp.clear();
                    }

                    tmp.push_back(abs(nums[i]-target[i]));
                    f = 0;
                }
                if(nums[i]<target[i])
                {
                    f = 1;
                    if(f1==1)
                    {
                        ans.push_back(tmp);
                        tmp.clear();
                    }

                    tmp.push_back(abs(nums[i]-target[i]));

                    f1 = 0; 
                }
           }
           if(tmp.size())
           ans.push_back(tmp);
         //  cout<<ans.size()<<endl;
           for(int i=0;i<ans.size();i++)
           {
              vector<int> tmp = ans[i];
            //   for(auto it: tmp)
            //   {
            //     cout<<it<<" ";
            //   }
            //   cout<<endl;
              if(tmp.size())
              {
                res+=tmp[0];
                for(int j=1;j<tmp.size();j++)
                {
                    if(tmp[j]>tmp[j-1])
                    {
                        res+=tmp[j]-tmp[j-1];
                    }
                }
              }


           }

           return res;


    }
};
