class Solution {
public:
    long long cc(vector<int> target)
    {
        int count = target[0];
        for (int i = 1; i < target.size(); i++)
            count += max(target[i] - target[i - 1], 0);
        return count;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        for(int i=0;i<n;i++) target[i]-=nums[i];
        vector<int> a;
        long long c = 0;
        vector<vector<int>> aa;
        for(int i=0;i<n;i++)
        {
            if(a.size()==0) a.push_back(target[i]);
            else if(a.back()<0 && target[i]<0) a.push_back(target[i]);
            else if(a.back()>=0 && target[i]>=0) a.push_back(target[i]);
            else 
            {
                aa.push_back(a);
                a.clear();
                a.push_back(target[i]);
            }
        }
        if(a.size()>0) 
            aa.push_back(a);
        for(auto &it:aa)
        {
            for(auto &i:it) i=abs(i);
        }
        for(auto &it:aa)
        {
            c+=cc(it);
        }
        return c;
    }
};
