const int N = 100010;
class Solution {
public:
    int d1[N], d2[N];
    int delta[N];
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        for (int i = 1; i <= n; i ++ ) {
            if (i == 1) d1[i] = nums[i - 1];
            else d1[i] = nums[i - 1] - nums[i - 2];
        }
        for (int i = 1; i <= n; i ++ ) {
            if (i == 1) d2[i] = target[i - 1];
            else d2[i] = target[i - 1] - target[i - 2];
        }

        for (int i = 1; i <= n; i ++ ) {
            delta[i] = d1[i] - d2[i];
        }

        long long x = 0, y = 0;
        long long res = 0;
        for (int i = 1; i <= n; i ++ ) {
            if (delta[i] < 0) x += -delta[i];
            else y += delta[i];
            if (min(x, y) > 0) {
                long long d = min(x, y);
                res += d;
                x -= d, y -= d;
            }
        }
        res += max(x, y);
        return res;

    }
};
