class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int>v;
        for (int i = 0; i < nums.size(); i++) v.push_back(target[i] - nums[i]);
        // for (int i = 0; i < v.size(); i++) cout<<v[i]<<" ";
        // cout<<endl;
        long long int ans = abs(v[0]) * 1LL;
        for (int i = 1; i < nums.size(); i++) {
            if (v[i - 1] > 0 && v[i] > 0) {
                if (v[i] >= v[i - 1]) ans += (v[i] - v[i - 1]) * 1LL;
            }
            else if (v[i - 1] < 0 && v[i] < 0) {
                if (abs(v[i]) >= abs(v[i - 1])) ans += (abs(v[i]) - abs(v[i - 1])) * 1LL;
            }
            else ans += abs(v[i]);
        }
        return ans;
    }
};
