class Solution {
    public long minimumOperations(int[] nums, int[] target) {

        int [] arr = new int [nums.length];

        for (int i = 0; i < nums.length; ++i) {
            arr[i] = target[i] - nums[i];
        }

        List<Integer> list = new ArrayList<>();

        boolean pos = true;
        boolean exp;

        long result = 0;

        for (int i = 0; i < arr.length; ++i) {
            if (arr[i] == 0) {
                result += solve(list);
                list.clear();
                pos = true;
            } else {
                exp = arr[i] > 0;

                if (exp != pos) {
                    result += solve(list);
                    list.clear();
                }

                list.add(Math.abs(arr[i]));
                pos = exp;
            }
        }

        result += solve(list);

        return result;
    }

    private long solve(List<Integer> list) {
        //System.out.println(list);
        int min = 0;
        long result = 0;

        for (int num : list) {
            if (num > min) {
                result += num - min;
            }

            min = num;
        }

        return result;
    }
}
