class Solution {
public:
    long long minimumOperations(vector<int>& a, vector<int>& b) {
        int n = a.size();
        vector<int> c;
        for (int i = 0; i < n; ++i)
            c.push_back(b[i] - a[i]);// cout << c[i] << ' '; cout << '\n';
        long long ans = 0;
        for (int l = 0, r; l < n;) {
            if (c[l] == 0) {
                ++l;
                continue;
            }
            for (r = l; r < n - 1 && (c[r + 1] > 0) == (c[l] > 0); ++r);
            // cout << l << ' ' << r << '\n';
            if (c[l] < 0)
                for (int i = l; i <= r; ++i) c[i] = -c[i];
            ans += c[l];
            for (int i = l + 1; i <= r; ++i)
                if (c[i] > c[i - 1]) ans += c[i] - c[i - 1];
            l = r + 1;
        }

        return ans;
    }
};
