class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
       long long ans=0;
       for(int i=0;i<n;){
        int st=i;
        int l1=0;
        vector<int>store;
        if(nums[i]==target[i]){++i;continue;}
       else if(target[i]>nums[i]){ while(i<n && target[i]>nums[i]){
            store.push_back(abs(target[i]-nums[i]));
            ++i;
        }
        }
        else {while(i<n && target[i]<nums[i]){
            store.push_back(abs(target[i]-nums[i]));
            ++i;
        }
        }
        for(int j=0;j<store.size();++j){
            if(j==0){
                ans+=store[j];
            }
            else {
                int l1=store[j];
                l1-=min(store[j],store[j-1]);
                ans+=l1;
            }
        }
       } 
       return ans;
    }
};
