class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        ans = 0

        d = [b-a for a, b in zip(nums, target)]
        n = len(d)
        i = 0
        question = []
        pre = 0
        while i < n:
            if pre * d[i] >= 0:
                if abs(d[i]) > abs(pre):
                    ans += abs(d[i] - pre)
                pre = d[i]
            else:
                ans += abs(d[i])
                pre = d[i]
            i += 1
        return ans
