class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long length = nums.size();
        vector<long long> dec(length + 2, 0);
        dec[0] = 0;
        dec[length + 1] = 0;
        long long result = 0;
        for(long long i = 0 ; i < length ; i ++){
            dec[i+1] = target[i] - nums[i];
        }
        for(long long i = 1 ; i < length + 2 ; i ++){
            result += abs(dec[i] - dec[i-1]);
        }
        return result / 2;
    }
};
