class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        dif = [i - j for i, j in zip(nums, target)]

        ans = 0
        stk = []
        for num in dif:
            if stk and stk[-1] == 0:
                stk.pop()
            if not stk:
                stk.append(num)
                continue
            if num > 0 and stk[-1] < 0:
                ans += -stk[-1]
                stk = [num]
                continue
            if num < 0 and stk[-1] > 0:
                ans += stk[-1]
                stk = [num]
                continue
            if stk[-1] >= 0:
                if stk[-1] >= num:
                    ans += stk[-1] - num
                    while stk and stk[-1] >= num:
                        stk.pop()
                stk.append(num)
            if stk[-1] < 0:
                if stk[-1] <= num:
                    ans += num - stk[-1]
                    while stk and stk[-1] <= num:
                        stk.pop()
                stk.append(num)
        ans += abs(stk[-1])
        return ans
