class Solution {
public:
    long long minimumOperations(vector<int> &nums, vector<int> &target) {
        adjacent_difference(nums.begin(), nums.end(), nums.begin());
        adjacent_difference(target.begin(), target.end(), target.begin());
        int n = nums.size();
        long long pos = 0, neg = 0;
        for (int i = 0; i < n; i++) {
            pos += nums[i] < target[i] ? target[i] - nums[i] : 0;
            neg += nums[i] > target[i] ? nums[i] - target[i] : 0;
        }
        return max(pos, neg);
    }
};
