class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long int ans=0;
        long long int c=0;
        int n=nums.size();
        long long int rc=0;
        for(int i=0; i<n; i++){
            rc=target[i]-nums[i];
            cout<<c<<' '<<rc<<' ';
            if(rc>=0 && c>=0){
                if(c>rc){
                    c=rc;
                }
                else{
                    ans+=rc-c;
                    c=rc;
                }
            }
            else if(rc<0 && c<0){
                if(abs(c)>abs(rc)){
                    c=rc;
                }
                else{
                    ans+=abs(c-rc);
                    c=rc;
                }
            }
            else{
                ans+=abs(rc);
                c=rc;
            }
            cout<<ans<<'\n';
        }
        return ans;

    }
};
