class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<long long> f(n + 5, 0);
        for (int i = 1; i <= n; i++) f[i] = nums[i - 1] - target[i - 1];
        long long postive = 0, pr = 0;
        for (int i = n; i; i--) f[i] -= f[i - 1];
        for (int i = 1; i <= n; i++)
            if (f[i] > 0) postive += f[i];
            else pr -= f[i];
        return max(pr, postive);
    }
};
