#define mii map<int,int>
#define vi vector<int>
#define vs vector<string>
#define vb vector<bool>
#define pii pair<int,int>
#define endl "\n"
#define intmax INT_MAX
#define intmin INT_MIN
#define need_for_speed ios_base::sync_with_stdio(false); cin.tie(NULL);
#define ff(i,a,b) for(int i=a;i<b;i++)
#define rfor(i,a,b) for(int i=a;i>=b;i--)
#define all(x) x.begin(),x.end()
#define pt(x) { cout<<x<<"\n"; }
#define fs first.second
#define ss second.second
#define countofSet(x) __builtin_popcount(x)
#define pb push_back
#define fir first
#define sec second
#define sqrt(x) sqrtl(x)
#define ll long long


class Solution {
public:
    long long minimumOperations(vector<int>& v, vector<int>& t)
    {
        ll ans = 0, idx = 0, n = v.size();
        vi d; ff(i,0,n) d.push_back(t[i] - v[i]);
        while(idx < n)
        {
            vi cur; cur.pb(d[idx]);
            ll j = idx;

            if( d[idx] < 0 )
            {
                while(j+1<n && d[j+1] < 0) 
                {
                    cur.pb( d[j+1] );
                    j++;
                }

                ff(i,0,cur.size()) {
                    cur[i] = cur[i] * -1;
                }

            }else
            {
                while(j+1<n && d[j+1] >= 0)
                {
                    cur.pb( d[j+1] );
                    j++;
                }

            }

            ll m = cur.size();
            ll temp = cur[0];
            for(int i=1;i<m;i++)
            {
                int add = cur[i] - cur[i-1];
                temp = temp + ( add >= 0 ? add : 0 );
            }
            ans = ans + temp;
            idx = j+1;
        }

        return ans;
    }
};
