class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        ans = 0
        last = target[0] - nums[0]
        for i, j in zip(nums, target):
            # print(last, ans)
            if (last < 0 and j - i > 0) or (last > 0 and j - i < 0):
                ans += abs(last)
            elif abs(last) > abs(j-i):
                ans += abs(last) - abs(j-i)

            last = j - i
            # print(last, ans)


        ans += abs(last)

        return ans
