class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0ll, now = 0ll;
        for(int i = 0; i < nums.size(); ++i){
            int d1, d2;
            if(i == 0) d1 = nums[i], d2 = target[i];
            else d1 = nums[i] - nums[i - 1], d2 = target[i] - target[i - 1];
            if(d1 == d2) continue;
            if(d1 > d2){
                if(now > d1 - d2) ;
                else if(now > 0) ans += 1ll * (d1 - d2 - now);
                else ans += 1ll * (d1 - d2);
                now -= (d1 - d2);
            }
            else {
                if(now < d1 - d2) ;
                else if(now < 0) ans += 1ll * (d2 - d1 + now);
                else ans += 1ll * (d2 - d1);
                now += d2 - d1;
            }
        }
        return ans;
    }
};
