class Solution {
public:
    using ll=long long;
    long long minimumOperations(vector<int>& a, vector<int>& b) {
        int n=a.size();
        ll res=0;
        if(a[0]>b[0]) res+=a[0]-b[0];
        for(int i=1; i<n; i++){
            int x=a[i]-a[i-1];
            int y=b[i]-b[i-1];
            if(x>y) res+=x-y;
        }
        if(-a.back()>-b.back()) res+=abs(a.back()-b.back());
        return res;
    }
};
