typedef long long ll;
class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        ll cnt = 0;
        ll prev_op = -1;
        vector<ll> ops;
        for (ll i=0; i<nums.size(); i++){
            ops.push_back(target[i] - nums[i]);
        }
        ll curr = ops[0];
        for (ll i=1; i<ops.size(); i++){
            if ((ops[i] < 0 && curr >= 0) || (ops[i] >=0 && curr < 0)){
                cnt+=abs(curr);
                curr = ops[i];
            }
            else{
                if (curr < 0){
                    if (ops[i] < curr) curr = ops[i];
                    else{
                        cnt+=abs(curr-ops[i]);
                        curr = ops[i];
                    }
                }
                else{
                    if (ops[i] > curr) curr = ops[i];
                    else{
                        cnt+=abs(curr-ops[i]);
                        curr = ops[i];
                    }
                }
            }
        }
        cnt+=abs(curr);
        return cnt;
    }
};
