class Solution {
public:
    long long minimumOperations(vector<int>& v, vector<int>& t) {
        long long last=0, ans=0,n=v.size();
        for(long long i=0; i<n; i++){
            long long d = t[i]-v[i];
            if(d != last) {
                if(last < 0 && d < 0) {
                    if(last>d) ans+=abs(d-last);
                }
                else if (last>0 && d>0) {
                    if(last<d) ans+=abs(d-last);
                }
                else ans+=abs(d);
            }
            // cout<<d<<" ";
            last = d;
        }
        return ans;
    }
};
