class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0;
        int N = (int)nums.size();
        vector<int> sta(1);
        for (int i = 0; i < N; i++){
            if (target[i] == nums[i]){
                sta.clear();
                sta.push_back(0);
            }
            else if (target[i] < nums[i]){
                bool flag = true;
                while (sta.size() && (sta.back() > 0 || sta.back() < target[i] - nums[i])){
                    if (sta.back() < target[i] - nums[i]) flag = false;
                    sta.pop_back();
                }
                if (flag) ans += sta.back() - target[i] + nums[i];
                sta.push_back(target[i] - nums[i]);
            }
            else{
                bool flag = true;
                while (sta.size() && (sta.back() < 0 || sta.back() > target[i] - nums[i])){
                    if (sta.back() > target[i] - nums[i]) flag = false;
                    sta.pop_back();
                }
                if (flag) ans += target[i] - nums[i] - sta.back();
                sta.push_back(target[i] - nums[i]);
            }
        }

        return ans;
    }
};
