class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<long long> diff(n,0);
        for(int i = 0; i < n; ++i){
            diff[i] = target[i] - nums[i];
        }
        long long operations = abs(diff[0]);
        for(int i = 1; i < n; ++i){
            if(diff[i] < 0 and diff[i-1] < 0){
                if(diff[i] < diff[i-1]){
                    operations += (diff[i-1] - diff[i]);
                }
                continue;
            }
            if(diff[i] > 0 and diff[i-1] > 0){
                if(diff[i] > diff[i-1]){
                    operations += (diff[i] - diff[i-1]);
                }
                continue;
            }
            operations += abs(diff[i]);
        }
        return operations;
    }
};
