def remove_consecutive_duplicates(lst):
    res = []
    for x in lst:
        if res and x == res[-1]:
            continue
        res.append(x)
    return res


class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diff = [a-b for a,b in zip(nums, target)]

        # print()
        # print(diff)

        if diff.count(0) == len(diff):
            print('exit')
            return 0

        diff = remove_consecutive_duplicates(diff)
        # print(diff)

        diff = [0,0,0,0] + diff + [0,0,0,0]

        res = 0

        arr = [0]
        for a,b,c,d in zip(diff, diff[1:], diff[2:], diff[3:]):
            if a < b and b > c:
                arr.append(b)
            if a > b and b < c:
                arr.append(b)
        arr.append(0)

        # print(arr)

        for a,b in zip(arr[1::2], arr[2::2]):
            if a < b:
                res += b-a
            else:
                res += a-b
        return res
