class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        for (int i = 0; i < target.size(); ++i) target[i] -= nums[i];
        long long ans = 0;


        long long d = 0;

        for (int i = 0; i < target.size(); ++i) {
            cout << i << " " << target[i] << " " << d << endl;
            if (target[i] == 0) {
                d = 0;
                continue;
            }
            if (target[i] > 0) {
                if (d <= 0) {
                    ans += target[i];
                    d = target[i];
                    continue;
                }
                if (d < target[i]) {
                    ans += target[i] - d;
                    d = target[i];
                    continue;
                }
                d = target[i];
            } else {
                if (d >= 0) {
                    ans -= target[i];
                    d = target[i];
                    continue;
                }
                if (abs(d) < abs(target[i])) {
                    ans += abs(target[i] - d);
                    d = target[i];
                    continue;
                }
                d = target[i];

            }
        }

        return ans;


    }
};
