class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<long long> d;
        for (int i = 0; i < n; ++i) {
            d.push_back(target[i] - nums[i]);
        }
        long long ans = abs(d[0]);
        for (int i = 1; i < n; ++i) {
            if (((d[i] >= 0) && (d[i - 1] <= 0)) || ((d[i] <= 0) && (d[i - 1] >= 0))) {
                ans += abs(d[i]);
            } else {
                if (abs(d[i]) > abs(d[i - 1])) ans += abs(d[i]) - abs(d[i - 1]);
            }
        }
        return ans;
    }
};
