class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        long ans = 0l;
        long prevDiff = 0l;
        for(int i = 0; i < nums.length; i++) {
            int diff = target[i] - nums[i];

            if(diff*prevDiff > 0) {
                if(Math.abs(diff) > Math.abs(prevDiff)) {
                    ans += Math.abs(diff - prevDiff);
                }
                prevDiff = diff;
            } else {
                prevDiff = diff;
                ans += Math.abs(diff);
            }
            // System.out.println(diff+" "+prevDiff+" "+ans);


        }
        return ans;
    }
}
