class Solution {
public:
    vector<int> arr;
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0;
        int n = nums.size();
        long long z = 0, f = 0;
        for(int i = 0; i < n; i ++){
            long long xs = target[i] - nums[i];
            if(xs == 0){
                z = f = 0; 
                continue;
            }
            if(xs < 0){
                z = 0;
                ans += max((long long)(abs(xs) - f), (long long)0);
                f = abs(xs);
            }else{
                f = 0;
                ans += max((long long)(xs - z), (long long)0);
                z = xs;
            }
        }
        return ans;
    }
};
