#define ll long long
class Solution {
public:
    int sgn(int x) {
        if (x<0) return -1;
        if (x>0) return 1;
        return 0;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        vector<int> deltas;
        for(int i=0;i<n;i++) {
            deltas.push_back(nums[i]-target[i]);
        }
        ll ans=0;
        for(int i=0;i<deltas.size();i++) {
            if (i>0 && sgn(deltas[i])==sgn(deltas[i-1])) {
                ans += max(0,abs(deltas[i])-abs(deltas[i-1]));
            } else {
                ans += abs(deltas[i]);
            }
        }
        return ans;



    }
};
