class Solution {
public:
	long long f(vector<int> &A){
		A.push_back(0);
		// for(auto x : A){printf("%d ",x);}printf("\n");
		vector<int> B = {0};
        long long ans = 0;
		int n = 0;
		for(int i=0;i<A.size();i++){
			while(B.back() > A[i]){
				n = max(n,A[i]);
				B.pop_back();
			}
			// printf("%d %d %d\n",n,i,A[i]);
			if(n > A[i]){ans += n-A[i];}
			if(B.back() < A[i]){
				B.push_back(A[i]);
			}
			n = A[i];
		}
		return ans;
	}
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
		int i, j;
		for(i=0;i<nums.size() && nums[i]==target[i]; i++){}
		long long ans=0;
		for(;i<nums.size();){
			vector<int> A = {0};
			for(j=i;j<nums.size() && ((nums[i]>target[i] && nums[j]>target[j]) || (nums[i]<target[i] && nums[j]<target[j]));j++){
				A.push_back(abs(nums[j]-target[j]));
			}
			ans += f(A);
			for(i=j;i<nums.size() && nums[i]==target[i];i++){}
		}
		return ans;
    }
};
