using ll = long long;
class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<ll>res;
        int n = nums.size();
        long long ans = 0;
        for(int i = 0; i < n; i ++){
            res.push_back(nums[i] - target[i]);
        }
        for(int i = 0; i < n; i ++){
            if(i == 0)ans += abs(res[i]);
            else{
                if(res[i] * res[i - 1] <= 0) ans += abs(res[i]);
                else{
                    ll t1 = abs(res[i]), t2 = abs(res[i - 1]);
                    // printf("t1 = %d t2 = %d\n",t1,t2);
                    ans += max(t1 - t2, 0ll);
                }
            }
        }
        return ans;
    }
};
