class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        ans = 0
        left, right = 0, 0
        while left < n:
            while nums[left] < target[left]:
                right = left+1
                while right < n and nums[right] < target[right]:
                    right += 1
                max_add = target[left] - nums[left]
                for i in range(left, right):
                    max_add = min(max_add, target[i]-nums[i])
                ans += max_add
                for i in range(left, right):
                    nums[i] += max_add
            left += 1
        left, right = 0, 0
        while left < n:
            while nums[left] > target[left]:
                right = left+1
                while right < n and nums[right] > target[right]:
                    right += 1
                max_minus = - target[left] + nums[left]
                for i in range(left, right):
                    max_minus = min(max_minus, -target[i]+nums[i])
                ans += max_minus
                for i in range(left, right):
                    nums[i] -= max_minus
            left += 1
        return ans
