class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        d = [target[i] - nums[i] for i in range(len(nums))]
        res = 0
        prev = 0
        for i,delta in enumerate(d):
            if prev > 0:
                if delta > 0:
                    res += max(0, delta - prev)
                else:
                    res += abs(delta)
            else:
                if delta < 0:
                    res += abs(min(0, delta - prev))
                else:
                    res += delta
            prev = delta

        return res
