def work(a):
    last = 0
    n = len(a)
    ans = 0
    for i in range(n):
        ans += max(a[i] - last, 0)
        last = a[i]
    return ans


class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        a = [u - v for u, v in zip(nums, target)]
        n = len(a)
        b = []
        cur = []
        cur_positive = None
        for i in range(n):
            if not cur:
                if a[i] == 0:
                    continue
                elif a[i] > 0:
                    cur_positive = True
                    cur.append(a[i])
                else:
                    cur_positive = False
                    cur.append(-a[i])
            else:
                if a[i] == 0:
                    b.append(cur)
                    cur = []
                elif a[i] > 0:
                    if cur_positive:
                        cur.append(a[i])
                    else:
                        b.append(cur)
                        cur_positive = True
                        cur = [a[i]]
                else:
                    if cur_positive:
                        b.append(cur)
                        cur_positive = False
                        cur = [-a[i]]
                    else:
                        cur.append(-a[i])
        if cur:
            b.append(cur)
        ans = 0
        # print(b)
        for cur in b:
            ans += work(cur)
        return ans
