class Solution {
public:
    long long minimumOperations(vector<int>& s, vector<int>& t) {
        long cnt=0;
        int n=s.size();

        long carry=0;
        bool f=1;
        for(int i=0;i<n;i++){
            if(t[i]>=s[i] ){
                int diff = t[i]-s[i];
                if(!f){
                    cnt+=diff;
                    carry=diff;
                    f=1;
                    continue;
                }
                if(diff<carry){
                    carry=diff;
                }
                else{
                    cnt+=(diff-carry);
                    carry=diff;
                }
                f=1;
            }
            else{
                int diff = t[i]-s[i];
                if(f){
                    cnt+=abs(diff);
                    carry=diff;
                    f=0;
                    continue;
                }
                if(diff>carry){
                    carry=diff;
                }
                else{
                    cnt+=abs(diff-carry);
                    carry=diff;
                }
                f=0;
            }
        }
        return cnt;
    }
};
