class Solution {
    int diff[100005];
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        for(int i=0;i<n;i++) diff[i] = target[i] - nums[i];
        int now = 0;
        long long ans = 0;
        for(int i=0;i<n;i++){
            // ans += abs(diff[i]-now);
            // now = diff[i];
            if(now == 0){
                now = diff[i];
                ans += abs(now);
            }else if(now>0){
                if(diff[i]>=0){
                    if(diff[i]>now){
                        ans += diff[i]-now;
                        now = diff[i];
                    }else{
                        now = diff[i];
                    }
                }else{
                    now = diff[i];
                    ans += abs(now);
                }
            }else{
                // now < 0;
                if(diff[i]<=0){
                    if(diff[i]<now){
                        ans += abs(diff[i]-now);
                        now = diff[i];
                    }else{
                        now = diff[i];
                    }
                }else{
                    now = diff[i];
                    ans += abs(now);
                }
            }
        }
        return ans;

    }
};
