class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)

        ans = pre = 0
        for i in range(n):
            acutal = nums[i] + pre
            if pre >= 0:
                if target[i] - nums[i] >= 0:
                    diff = target[i] - nums[i]
                    if diff <= pre:
                        pre = diff
                    else:
                        ans += diff - pre
                        pre = diff
                else:
                    pre = target[i] - nums[i]
                    ans += abs(pre)
            else:
                if target[i] - nums[i] <= 0:
                    diff = target[i] - nums[i]
                    if diff >= pre:
                        pre = diff
                    else:
                        ans += pre - diff
                        pre = diff
                else:
                    pre = target[i] - nums[i]
                    ans += abs(pre)
        return ans
