from typing import List


class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        a = []
        for i, j in zip(nums, target):
            a.append(i - j)
        t = 0
        for i,j in zip( [0]+a, a+[0]):
            t+=abs(i-j)
        return t//2
