class Solution {
public:
    long long minimumOperations(vector<int>& a, vector<int>&b) {
        int n = a.size();
        vector<int> ta = a, tb = b;
        long long p = 0, q = 0, ans = 0;
        for(int i = 0; i < n; ++i)
        {
            if(i) a[i] = ta[i] - ta[i - 1], b[i] = tb[i] - tb[i - 1];
            else a[i] = ta[i], b[i] = tb[i];
            if(a[i]!=b[i])
            {
                if(a[i] < b[i])
                {
                        long long v = b[i] - a[i];
                        int t = min(q, v);
                        q-=t;
                        v-=t;
                        if(v) p+=v,ans+=v;

                }
                else
                {
                    long long v = a[i] - b[i];
                        int t = min(p, v);
                        p-=t;
                        v-=t;
                        if(v) q+=v,ans+=v;
                }
            }
        }
        return ans;
    }
};
