class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        const int n = nums.size();
        long long result = 0;
        int last_diff = 0;
        for (int i = 0; i < n; ++i) {
            const int diff = target[i] - nums[i];
            if (diff != 0) {
                if ((last_diff < 0 && diff > 0 ) || (last_diff > 0 && diff < 0)) {
                    result += abs(diff);
                } else if (abs(last_diff) < abs(diff)) {
                    result += abs(diff) - abs(last_diff);
                }
            }
            last_diff = diff;
        }
        return result;
    }
};
