class Solution {
public:

    long long solve(vector<int>&nums){

        long long count=nums[0];

        for(int i=1;i<nums.size();i++){
            long long diff=nums[i]-nums[i-1];

            count=count+max((long long)0,diff);
        }

        return count;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {

        int n=nums.size();
        vector<int> pos(n,0);
        vector<int> neg(n,0);

        for(int i=0;i<n;i++){
            int val=target[i]-nums[i];
            if(val>0){
                pos[i]=val;
            }
            else neg[i]=-val;
        }

        long long ans=0;
        ans=ans+solve(pos);
        ans=ans+solve(neg);



        return ans;
    }
};
