class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ops=0;
        int diff;
        int end;

        for(int i=0;i<target.size();i++)
        {
            if(nums[i]==target[i])
                continue;
            diff = abs(target[i]-nums[i]);
            end=i;

            if(nums[i]>target[i])
            {
                while(end<target.size()&&nums[end]>=target[end])
                    end++;
            }
            else
            {
                while(end<target.size()&&nums[end]<=target[end])
                    end++;
            }

            normal(nums,target,i,end,diff);
            ops+=diff;
        }

        return ops;
    }

    void normal(vector<int>& nums,vector<int>& target,int start,int end,int diff)
    {
        for(int i=start;i<end&&i<target.size();i++)
        {
            if(abs(target[i]-nums[i])<=diff)
            {
                diff=abs(nums[i]-target[i]);
                nums[i]=target[i];
            }
            else
            {
                if(target[i]>nums[i])
                    nums[i]+=diff;
                else
                    nums[i]-=diff;
            }
        }
    }
};
