class Solution {
public:
    long long minimumOperations(vector<int>& a, vector<int>& t) {

        long long ans=0;

        int cur=0;

        int n=a.size();

        for(int i=0;i<n;i++){

            int d=t[i]-a[i];

            if(d>=0){

                int st=max({0,min(cur,d)});                
                int dif=d-st;
                cur=st+dif;
                                // cout<<"st is "<<st<<" cur is "<<cur<<"\n";

                ans+=dif;
            }
            else
            {
                int st=min({0,max(cur,d)});

                int dif=abs(d-st);
                cur=st-dif;

                // cout<<"st is "<<st<<" cur is "<<cur<<"\n";
                ans+=dif;
            }
        }
        return ans;
    }
};
