class Solution:
    def minimumOperations(self, A: List[int], B: List[int]) -> int:
        N = len(A)
        C = [0] * (N + 2)
        for i in range(N):
            C[i+1] = B[i] - A[i]

        def work(row):
            ans = row[0]
            for i in range(1, len(row)):
                ans += max(row[i] - row[i-1],0)
            return ans

        ans = 0
        for k, grp in groupby(C, lambda x: -1 if x<0 else 0 if x==0 else 1):
            if k:
                ans += work([abs(x) for x in grp])
        return ans
