class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();

        long long c=0;

        vector<vector<int>> ans;

        int d=target[0]-nums[0];
        bool flag;

        if(d>=0)
            flag=true;
        else
            flag=false;

        vector<int> v;

        for(int i=0;i<n;i++){

            int diff=target[i]-nums[i];

            if(diff>0){
                if(!flag){
                    ans.push_back(v);
                    v.clear();
                    flag=true;
                }
                v.push_back(diff);
            }else{
                if(flag){
                    ans.push_back(v);
                    v.clear();
                    flag=false;
                }
                v.push_back(diff);
            }

        }

        ans.push_back(v);

        auto func=[&](vector<int> &v){

            int n=v.size();

            long long ans=0;
            long long c=0;

            for(int i=0;i<n;i++){
                v[i]=abs(v[i]);
                // cout<<v[i]<<" ";
            }
            // cout<<endl;



            for(int i=0;i<n;i++){

                if(c<v[i]){
                    ans+=(v[i]-c);
                    c=v[i];
                }

                c=v[i];
            }

            return ans;
        };

        for(auto &v:ans){

            c+=func(v);
        }

        return c;
    }
};
