class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        vector<long long int> diff(n);
        for(int i=0; i < n; i++){
            diff[i]=target[i]-nums[i];
        }
        long long int ans=0;
        int last=-1;
        for(int i=0; i < n; i++){
            if(last==-1){
                ans+=abs(diff[i]);
                if(diff[i]<0)last=0;
                else last=1;
            }else if(last==1){
                if(diff[i]>diff[i-1])ans+=diff[i]-diff[i-1];
                else if(diff[i]<0){
                    ans+=abs(diff[i]);
                    last=0;
                }else{
                    last==-1;
                }
            }else{
                if(diff[i]<diff[i-1])ans+=diff[i-1]-diff[i];
                else if(diff[i]>0){
                    last=1;
                    ans+=diff[i];
                }else{
                    last==-1;
                }
            }
        }

        return ans;


    }
};
